CREATE TABLE "DIALOGO" ("id" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL , "personaje" INTEGER, "etapa" INTEGER, "texto" VARCHAR);
CREATE TABLE "ETAPA" ("id" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL , "mision" INTEGER, "orden" INTEGER, "texto" VARCHAR);
CREATE TABLE "MAPA" ("id" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL , "imagen" VARCHAR NOT NULL  UNIQUE );
CREATE TABLE "MISION" ("id" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL , "nombre" VARCHAR);
CREATE TABLE "NEXO" ("id" VARCHAR PRIMARY KEY  NOT NULL ,"mapa" INTEGER NOT NULL ,"posx" INTEGER NOT NULL ,"posy" INTEGER NOT NULL, "direccion" INTEGER NOT NULL ,"enlace" VARCHAR NOT NULL);
CREATE TABLE "OBSTACULO" ("id" INTEGER PRIMARY KEY  NOT NULL ,"mapa" INTEGER,"posx" INTEGER NOT NULL ,"posy" INTEGER NOT NULL ,"ancho" INTEGER NOT NULL ,"alto" INTEGER NOT NULL );
CREATE TABLE "PDINAMICO" ("id" INTEGER PRIMARY KEY  NOT NULL ,"imagen" VARCHAR NOT NULL ,"nombre" VARCHAR,"mapa" INTEGER NOT NULL ,"posx" INTEGER NOT NULL ,"posy" INTEGER NOT NULL ,"enemigo" BOOL NOT NULL ,"vida" INTEGER DEFAULT (1) );
CREATE TABLE "PESTATICO" ("id" INTEGER PRIMARY KEY  NOT NULL ,"imagen" VARCHAR NOT NULL ,"nombre" VARCHAR NOT NULL ,"mapa" INTEGER NOT NULL ,"posx" INTEGER NOT NULL ,"posy" INTEGER NOT NULL ,"vida" INTEGER DEFAULT (20) , "maxvida" INTEGER);
